		// TODO: Make a nested ul-li based on the levels in the document tree.
		foreach ($docs as $doc)
		{
			$s  = "  <li class=\\"sitemap\\">";
			$s .= "<a href=\\"[(site_url)][~" . $doc[\'id\'] . "~]\\" class=\\"sitemap\\">" . $doc[\'pagetitle\'] . "</a>";
			$s .= "</li>\\n";
			$output .= $s;
		} // end foreach
		$output .= "</ul>\\n";
		break;
		
	case \'txt\': // plain text list of URLs

		foreach ($docs as $doc)
		{
			$url = \'[(site_url)][~\'.$doc[\'id\'].\'~]\';

			$output .= $url."\\n";
